import logo from './logo.svg';
import './App.css';
import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLinkedin } from '@fortawesome/free-brands-svg-icons'
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons'

const todoItems = [
  {
    id: 1,
    title: "Go to Market",
    description: "Buy ingredients to prepare dinner",
    completed: true,
  },
  {
    id: 2,
    title: "Study",
    description: "Read Algebra and History textbook for the upcoming test",
    completed: false,
  },
  {
    id: 3,
    title: "Sammy's books",
    description: "Go to library to return Sammy's books",
    completed: true,
  },
  {
    id: 4,
    title: "Article",
    description: "Write article on how to use Django with React",
    completed: false,
  }
]

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      todoList: [],
      modal: false,
      activeItem: {
        title: "",
        description: "",
        completed: "",
      },
      email: {
        contactName: "",
        email: "",
        message: ""
      },
      urlRoot: "http://ec2-3-134-112-63.us-east-2.compute.amazonaws.com:8000",
      // urlRoot: "http://localhost:8000",
    };
  }

  // componentDidMount() {
  //   this.refreshList();
  // }




  sendEmail = (json) => {
    console.log(json)
    let res = axios
      .post(this.state.urlRoot + "/sendEmail", json)
      .then((res => console.log(res)))
      .catch((err) => console.log(err));
    console.log(res);
  }

  refreshList = () => {
    axios
      .get("/api/todos")
      .then((res => this.setState({ todoList: res.data })))
      .catch((err) => console.log(err));
  }

  toggle = () => {
    this.setState({ modal: !this.state.modal })
  }

  handleSubmit = (item) => {
    this.toggle();

    if (item.id) {
      axios
        .put(`/api/todos/${item.id}/`, item)
        .then((res) => this.refreshList());
      return;
    }
    axios
      .post("/api/todos/", item)
      .then((res) => this.refreshList());
  };

  handleDelete = (item) => {
    axios
      .delete(`/api/todos/${item.id}/`)
      .then((res) => this.refreshList());
  }

  createItem = () => {
    const item = { title: "", description: "", completed: false };

    this.setState({ activeItem: item, modal: !this.state.modal })
  }

  editItem = (item) => {
    console.log(item);
    console.log(this.state)    
    // this.setState({ activeitem: item, modal: !this.state.modal, activeitem: {...this.state.someProperty,item.title} , description: item.description})
    this.setState({ someProperty: { ...this.state.someProperty, flag: false} });
  }

  displayCompleted = (status) => {
    if (status) {
      return this.setState({ viewCompleted:true });
    }

    return this.setState({ viewCompleted: false })
  };

  renderTablist = () => {
    return (
      <div className='nav nav-tabs'>
          <span className={this.state.viewCompleted ? "nav-link active" : "nav-link"}
          onClick={() => this.displayCompleted(true)}
          >Complete</span>
          <span 
            className={this.state.viewCompleted ? "nav-link" : "nav-link active"}
            onClick={() => this.displayCompleted(false)}
            >
              Incomplete
            </span>
      </div>
    )
  }

  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.todoList.filter(
      (item) => item.completed == viewCompleted)
    ;


  return newItems.map((item) => (
    <li key= {item.id}
      className="list-group-item d-flex justify-content-between align-items-center"
    >
      <span className={`todo-title mr-2 ${
        this.state.viewCompleted ? "compeleted-todo" : ""
      }`}
      title={item.description}
      >
        {item.title}
      </span>
      <span>
        <button
          className='btn bten-secondary mr-2'
          onClick={() => this.editItem(item)}
          >
            Edit
          </button>
          <button
            className='btn btn-danger'
            onClick={() => this.handleDelete(item)}
            >
              Delete
          </button>
      </span>
    </li>
  ));
    }


render() {
  return (
    <div id="absoluteMain">
      <Header></Header>
      <Banner></Banner>
      <About></About>
    </div>


  )
}

  }

  class Header extends Component {
    constructor(props){
      super(props)
        this.state = {

        }
      }

    render(){
      return(
        <div>
          <div id="headerBar">
              <div id="headerDiv">
                <img id="headerLogo" src="Revenue3Logo.jpg"></img>
              </div>
              <div id="headerLinkedIn"><a id="buttonLink" href="https://www.linkedin.com/company/revenue3official">
                <FontAwesomeIcon icon={faLinkedin} />
                </a>
              </div>
              <div id="headerMail"> <a id="buttonLink" href="https://revenue3.com/contact-us/">
                <FontAwesomeIcon icon={faPaperPlane} />
                </a>
              </div>
              <div className="lineFour">
              <button id="buttonHeader"><a id="buttonLink" href="https://revenue3.com/contact-us/">Get Started</a></button>
            </div>


         </div>
        </div>
      )
    }
  }

  class Banner extends Component {
    constructor(props){
      super(props)
      this.state = {
      }
    }

    render(){
      return(
        <div>
          <div className="firstImages">
            <img id="initialBackgroundPic" src="blockcnnhain_hero_bg.png"></img>
          </div>
          <div className="boxLander">
            <div className="lineOne">
              Welcome To
            </div>
            <div className="lineTwo">
              Revenue3
            </div>
            <div className="lineThree">
              Your inside source for crypto-native GTM talent
            </div>
            <div className="lineFour">
              <button id="buttonActual">Get Started</button>
            </div>
          </div>
          <div className="lineFive">
                <img id="overlayBackgroundPic" src="r3landing.png"></img>
            </div>
        </div>
      )
    }
  }

  class About extends Component {
    constructor(props){
      super(props)
      this.state = {
        tiles: [
          {
          id:0,
          title: "About Us",
          text: "In a landscape where conventional recruiting strategies often overlook the nuances of sourcing candidates, Revenue3 was founded on the conviction that the crypto industry demands a specialized approach to talent acquisition for go-to-market roles. Our mission is to redefine the standard of building GTM teams within this rapidly evolving industry.",
          imageLink: "gfx-b.png"
          }
        ]
      }
    }
    render(){
      return(
        <div>
          {this.state.tiles.map((tiles, index) => {
            return(
              <div id="aboutSection">
                <div id="textSectionAbout">
                  <div id="aboutTitle" key={index + 1}>
                    <div>{tiles.title}</div>
                  </div>
                  <div id="aboutDiv" key={index + 2}>
                    <div>{tiles.text}</div>
                  </div>
                </div>
                <div class="aboutUsImg" key={index + 3}>
                  <img src={tiles.imageLink} id="tileImage"></img>
                </div>
                <div class="backgroundImageAboutLeft" key={index + 4}>
                  <img src="ddf_01.png" id="tileImageAboutleft"></img>
                </div>

              </div>
            )
          })}
        </div>
      )
    }
  }

  class WhyRev3 extends Component {
    constructor(props){
      super(props)
      this.state = {
        tiles: [
          {
