import logo from './logo.svg';
import './App.css';
import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

const todoItems = [
  {
    id: 1,
    title: "Go to Market",
    description: "Buy ingredients to prepare dinner",
    completed: true,
  },
  {
    id: 2,
    title: "Study",
    description: "Read Algebra and History textbook for the upcoming test",
    completed: false,
  },
  {
    id: 3,
    title: "Sammy's books",
    description: "Go to library to return Sammy's books",
    completed: true,
  },
  {
    id: 4,
    title: "Article",
    description: "Write article on how to use Django with React",
    completed: false,
  }
]

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      todoList: [],
      modal: false,
      activeItem: {
        title: "",
        description: "",
        completed: "",
      }
    };
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
      .get("/api/todos")
      .then((res => this.setState({ todoList: res.data })))
      .catch((err) => console.log(err));
  }

  toggle = () => {
    this.setState({ modal: !this.state.modal })
  }

  handleSubmit = (item) => {
    this.toggle();

    if (item.id) {
      axios
        .put(`/api/todos/${item.id}/`, item)
        .then((res) => this.refreshList());
      return;
    }
    axios
      .post("/api/todos/", item)
      .then((res) => this.refreshList());
  };

  handleDelete = (item) => {
    axios
      .delete(`/api/todos/${item.id}/`)
      .then((res) => this.refreshList());
  }

  createItem = () => {
    const item = { title: "", description: "", completed: false };

    this.setState({ activeItem: item, modal: !this.state.modal })
  }

  editItem = (item) => {
    console.log(item);
    console.log(this.state)    
    // this.setState({ activeitem: item, modal: !this.state.modal, activeitem: {...this.state.someProperty,item.title} , description: item.description})
    this.setState({ someProperty: { ...this.state.someProperty, flag: false} });
  }

  displayCompleted = (status) => {
    if (status) {
      return this.setState({ viewCompleted:true });
    }

    return this.setState({ viewCompleted: false })
  };

  renderTablist = () => {
    return (
      <div className='nav nav-tabs'>
          <span className={this.state.viewCompleted ? "nav-link active" : "nav-link"}
          onClick={() => this.displayCompleted(true)}
          >Complete</span>
          <span 
            className={this.state.viewCompleted ? "nav-link" : "nav-link active"}
            onClick={() => this.displayCompleted(false)}
            >
              Incomplete
            </span>
      </div>
    )
  }

  renderItems = () => {
    const { viewCompleted } = this.state;
    const newItems = this.state.todoList.filter(
      (item) => item.completed == viewCompleted)
    ;


  return newItems.map((item) => (
    <li key= {item.id}
      className="list-group-item d-flex justify-content-between align-items-center"
    >
      <span className={`todo-title mr-2 ${
        this.state.viewCompleted ? "compeleted-todo" : ""
      }`}
      title={item.description}
      >
        {item.title}
      </span>
      <span>
        <button
          className='btn bten-secondary mr-2'
          onClick={() => this.editItem(item)}
          >
            Edit
          </button>
          <button
            className='btn btn-danger'
            onClick={() => this.handleDelete(item)}
            >
              Delete
          </button>
      </span>
    </li>
  ));
    }


render() {
  return (
    <div id="absoluteMain">
      <main className='conatiner'>
      <h1 className="text-white text-uppercase text-center my-4">Todo App</h1>
      <div className='row'>
        <div className='col-md-6 col-sm-10 mx-auto p-0'>
          <div className='card p-3'>
            <div className="mb-4">
              <button
                className='btn btn-primary'
                onClick={this.createItem}
                >
                Add Task
              </button>
            </div>
            {this.renderTablist()}
            <ul className='list-group list-group-flush border-top-0'>
              {this.renderItems()}
            </ul>
          </div>
        </div>
      </div>
      {this.state.modal ? (
        <Modal 
          activeItem={this.state.activeItem}
          toggle={this.toggle}
          onSave={this.handleSubmit}
        />
      ) : null}
    </main>

    <About></About>
    </div>


  )
}

  }

  class About extends Component{
    constructor(props){
      super(props)
      this.state = {
        teamMembers: [
          {
            name: "Weiss",
            imageLink: "weiss.jpg",
            job: "CEO", 
          },
          {
            name: "Woods",
            imageLink: "../public/woods.jpg",
            job: "CTO", 
          },
          {
            name: "Lawyer Dude",
            imageLink: "lawyers.jpg",
            job: "CLO", 
          },
        ]
        }
      }

    render() {

      return(
        <div className="team">
          <div className="container">
            <h1 className="text-center">Our Team</h1>
            <div className="row">
            {this.state.teamMembers.map((index, person) =>{
              return (<div className="col-lg-3 col-md-3 col-sm-12 item">
                <img src={person.imageLink} className="img-fluid" alt="team" key={index}></img>
                <div className="des" key={index}>
                  {person.name}
                </div>
                <span className="text-muted" key={index}>{person.job}</span>
              </div>)
            })}
            </div>
          </div>
        </div>
      )
    }

  }




export default App;
